SET DEFINE OFF;DELETE FROM FLDMASTER WHERE 1 = 1 AND NVL(OBJNAME,'NULL') = NVL('6657','NULL');FROM dualunion allSELECT FILTERCD, VALUE, VALUECD, DISPLAY, EN_DISPLAY,DESCRIPTIONFROM(SELECT TO_CHAR(CFO.AUTOID) FILTERCD, TO_CHAR(CFO.AUTOID)VALUE, TO_CHAR(CFO.AUTOID) VALUECD,    CFO.BANKACC || '' - '' || CF.FULLNAME  DISPLAY,    CFO.BANKACC || '' - '' || CF.FULLNAME EN_DISPLAY,    CFO.BANKACC || '' - '' || CF.FULLNAME DESCRIPTIONFROM CFOTHERACC CFO, CFMAST CFWHERE CFO.CFCUSTID = CF.CUSTID    AND CFO.TYPE = 1    AND CF.CUSTODYCD = ''<$TAGFIELD>'')', 'N', NULL, 'Y', NULL, 'N', NULL, NULL, NULL, 'N', 17, 1);COMMIT;