SET DEFINE OFF;DELETE FROM RPTFIELDS WHERE 1 = 1 AND NVL(OBJNAME,'NULL') = NVL('BA6006','NULL');Insert into RPTFIELDS   (MODCODE, FLDNAME, OBJNAME, DEFNAME, CAPTION, EN_CAPTION, ODRNUM, FLDTYPE, FLDMASK, FLDFORMAT, FLDLEN, LLIST, LCHK, DEFVAL, VISIBLE, DISABLE, MANDATORY, AMTEXP, VALIDTAG, LOOKUP, DATATYPE, INVNAME, FLDSOURCE, FLDDESC, CHAINNAME, PRINTINFO, LOOKUPNAME, SEARCHCODE, SRMODCODE, INVFORMAT, TAGFIELD, TAGLIST, TAGVALUE, ISPARAM, CTLTYPE, CHKSCOPE) Values   ('BA', 'PV_ISSUERS', 'BA6006', 'PV_ISSUERS', 'Mã TCPH', 'Issuers', 0, 'M', NULL, NULL, 20, 'SELECT SHORTNAME VALUE,SHORTNAME VALUECD, FULLNAME DISPLAY,EN_FULLNAME EN_DISPLAY FROM ISSUERS', NULL, NULL, 'Y', 'N', 'Y', NULL, NULL, 'N', 'C', NULL, NULL, NULL, NULL, NULL, NULL, 'ISSUERS_SHORTNAME', 'SA', NULL, NULL, NULL, NULL, 'Y', 'M', 'N');Insert into RPTFIELDS   (MODCODE, FLDNAME, OBJNAME, DEFNAME, CAPTION, EN_CAPTION, ODRNUM, FLDTYPE, FLDMASK, FLDFORMAT, FLDLEN, LLIST, LCHK, DEFVAL, VISIBLE, DISABLE, MANDATORY, AMTEXP, VALIDTAG, LOOKUP, DATATYPE, INVNAME, FLDSOURCE, FLDDESC, CHAINNAME, PRINTINFO, LOOKUPNAME, SEARCHCODE, SRMODCODE, INVFORMAT, TAGFIELD, TAGLIST, TAGVALUE, ISPARAM, CTLTYPE, CHKSCOPE) Values   ('BA', 'PV_ISSUECODE', 'BA6006', 'PV_ISSUECODE', 'Mã đợt phát hành', 'Issuance', 1, 'M', NULL, NULL, 20, 'SELECT ISSUECODE VALUE,ISSUECODE VALUECD, AUTOID DISPLAY,AUTOID EN_DISPLAY FROM ISSUES', NULL, NULL, 'Y', 'N', 'Y', NULL, NULL, 'Y', 'C', NULL, NULL, NULL, NULL, NULL, NULL, 'ISSUES_TX', 'BA', NULL, NULL, NULL, NULL, 'Y', 'M', 'N');Insert into RPTFIELDS   (MODCODE, FLDNAME, OBJNAME, DEFNAME, CAPTION, EN_CAPTION, ODRNUM, FLDTYPE, FLDMASK, FLDFORMAT, FLDLEN, LLIST, LCHK, DEFVAL, VISIBLE, DISABLE, MANDATORY, AMTEXP, VALIDTAG, LOOKUP, DATATYPE, INVNAME, FLDSOURCE, FLDDESC, CHAINNAME, PRINTINFO, LOOKUPNAME, SEARCHCODE, SRMODCODE, INVFORMAT, TAGFIELD, TAGLIST, TAGVALUE, ISPARAM, CTLTYPE, CHKSCOPE) Values   ('BA', 'PV_CONTRACTNO', 'BA6006', 'PV_CONTRACTNO', 'Mã hợp đồng', 'Contract', 2, 'C', NULL, NULL, 50, NULL, NULL, NULL, 'Y', 'N', 'Y', NULL, NULL, 'Y', 'C', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PV_ISSUECODE', 'SELECT I1.CONTRACTNO VALUE, I1.CONTRACTNO VALUECD, I1.CONTRACTNO DISPLAY, I1.CONTRACTNO EN_DISPLAY, I1.CONTRACTNO DESCRIPTION
FROM
(
    SELECT ''-----'' CONTRACTNO, ''<$TAGFIELD>'' ISSUECODE
    FROM DUAL
    UNION ALL
    SELECT DISTINCT SB.CONTRACTNO, ISS.ISSUECODE
    FROM ISSUES ISS, BONDISSUE BI, sbsecurities SB
    WHERE ISS.AUTOID = BI.ISSUESID AND BI.BONDCODE = SB.CODEID AND CONTRACTNO IS NOT NULL
    UNION ALL
    SELECT DISTINCT IC.CONTRACTNO, ISS.ISSUECODE
    FROM ISSUES ISS, ISSUER_CONTRACTNO IC 
    WHERE ISS.AUTOID = IC.ISSUESID
) I1
WHERE I1.ISSUECODE = ''<$TAGFIELD>''', NULL, 'Y', 'C', 'N');Insert into RPTFIELDS   (MODCODE, FLDNAME, OBJNAME, DEFNAME, CAPTION, EN_CAPTION, ODRNUM, FLDTYPE, FLDMASK, FLDFORMAT, FLDLEN, LLIST, LCHK, DEFVAL, VISIBLE, DISABLE, MANDATORY, AMTEXP, VALIDTAG, LOOKUP, DATATYPE, INVNAME, FLDSOURCE, FLDDESC, CHAINNAME, PRINTINFO, LOOKUPNAME, SEARCHCODE, SRMODCODE, INVFORMAT, TAGFIELD, TAGLIST, TAGVALUE, ISPARAM, CTLTYPE, CHKSCOPE) Values   ('BA', 'I_DATE', 'BA6006', 'I_DATE', 'Ngày thanh toán', 'Payment date', 3, 'M', '99/99/9999', 'dd/MM/yyyy', 10, NULL, NULL, '<$BUSDATE>', 'Y', 'N', 'Y', NULL, NULL, 'N', 'D', NULL, NULL, NULL, NULL, NULL, NULL, 'BONDTYPEPAY_SRCH', 'BA', NULL, NULL, NULL, NULL, 'Y', 'T', 'N');COMMIT;