SET DEFINE OFF;DELETE FROM SEARCH WHERE 1 = 1 AND NVL(SEARCHCODE,'NULL') = NVL('APPENDIX_EX','NULL');Insert into SEARCH   (SEARCHCODE, SEARCHTITLE, EN_SEARCHTITLE, SEARCHCMDSQL, OBJNAME, FRMNAME, ORDERBYCMDSQL, TLTXCD, CNTRECORD, ROWPERPAGE, AUTOSEARCH, INTERVAL, AUTHCODE, ROWLIMIT, CMDTYPE, CONDDEFFLD, BANKINQ, BANKACCT) Values   ('APPENDIX_EX', 'Quản lý giao dịch phụ lục', 'all transact agrees management ', 'SELECT APAL.AUTOID,APAL.NO,APAL.NAME,APAL.BUYPARTY,CR.CUSTODYCD,CR.CODEID,CR.ACCTNO,APE.AQTTY,APE.AVALUE,APE.REAQTTY,APE.REAVALUE,APE.REMAQTTY,APE.REMAVALUE,APE.STATUS
  FROM
     (SELECT AP.CRPHYSAGREEID, AP.AUTOID,AP.NO,AP.NAME,AP.BANKACCTNO, AP.EFFDATE,CDCONTENT BUYPARTY,CDVAL BUYPARTYVAL
        FROM APPENDIX AP, ALLCODE AL 
        WHERE CDNAME=''BUYPARTY'' AND CDTYPE=''SE'' AND AL.CDVAL =AP.BUYPARTY) APAL, CRPHYSAGREE CR, APPENDIX_EX APE
  WHERE APAL.CRPHYSAGREEID =CR.AUTOID 
  AND APE.APPENDIXID(+)=APAL.AUTOID', 'SE.APPENDIX', '', '', '', 0, 5000, 'N', 1, '', 'Y', 'T', '', 'N', '');COMMIT;