SET DEFINE OFF;DELETE FROM SEARCH WHERE 1 = 1 AND NVL(SEARCHCODE,'NULL') = NVL('BONDTYPE','NULL');Insert into SEARCH   (SEARCHCODE, SEARCHTITLE, EN_SEARCHTITLE, SEARCHCMDSQL, OBJNAME, FRMNAME, ORDERBYCMDSQL, TLTXCD, CNTRECORD, ROWPERPAGE, AUTOSEARCH, INTERVAL, AUTHCODE, ROWLIMIT, CMDTYPE, CONDDEFFLD, BANKINQ, BANKACCT) Values   ('BONDTYPE', 'Khai báo nguyên tắc định giá', 'Price policy', 'SELECT BO.VALMETHOD, BO.ACTYPE, ISS.ISSUECODE, BO.TICKERLIST, BO.XPARAM, BO.YPARAM , BO.ISSUESID, A1.<@CDCONTENT> SECTYPECNT, A2.<@CDCONTENT> VALMETHOD_NAME
FROM BONDTYPE BO, ISSUES ISS, ALLCODE A1, ALLCODE A2
WHERE BO.ISSUESID = ISS.AUTOID AND
      A1.CDTYPE = ''SA'' AND A1.CDNAME = ''SECTYPE'' AND A1.CDVAL = BO.SECTYPE AND
      A2.CDTYPE = ''BA'' AND A2.CDNAME = DECODE(BO.SECTYPE,''111'',''STOCKMETHOD'',''222'',''BONDMETHOD'',''555'',''CASHMETHOD'','''') AND A2.CDVAL = BO.VALMETHOD', 'BONDTYPE', 'frmBONDTYPE', 'BO.ACTYPE DESC', '', 0, 5000, 'Y', 30, '', 'Y', 'T', '', 'N', '');COMMIT;