 from  vw_ddmast_all d,(select * from exchangerate where rtype =''TTM'' and ITYPE=''SHV'') e
 WHERE  STATUSCODE = ''A''
    and d.ccycd = e.currency(+)', 'DDMAST', '', '', '', 0, 5000, 'N', 1, '', 'Y', 'T', '', 'N', '');COMMIT;