SET DEFINE OFF;DELETE FROM SEARCH WHERE 1 = 1 AND NVL(SEARCHCODE,'NULL') = NVL('ISSUERS_BA6030','NULL');Insert into SEARCH   (SEARCHCODE, SEARCHTITLE, EN_SEARCHTITLE, SEARCHCMDSQL, OBJNAME, FRMNAME, ORDERBYCMDSQL, TLTXCD, CNTRECORD, ROWPERPAGE, AUTOSEARCH, INTERVAL, AUTHCODE, ROWLIMIT, CMDTYPE, CONDDEFFLD, BANKINQ, BANKACCT) Values   ('ISSUERS_BA6030', 'Quản lý tổ chức phát hành', 'Issuers management', 'SELECT ISS.ISSUERID, ISS.SHORTNAME,ISS.FULLNAME,ISS.EN_FULLNAME, ISS.OFFICENAME, ISS.CUSTID, ISS.ADDRESS,ISS.PHONE, ISS.FAX, 
A0.<@CDCONTENT> ECONIMIC,A1.<@CDCONTENT> BUSINESSTYPE,
ISS.BANKACCOUNT,ISS.BANKNAME,ISS.LICENSENO,ISS.LICENSEDATE,ISS.LINCENSEPLACE,
ISS.OPERATENO,ISS.OPERATEDATE,ISS.OPERATEPLACE,ISS.LEGALCAPTIAL,ISS.SHARECAPITAL,
    A2.<@CDCONTENT> MARKETSIZE,ISS.PRPERSON,ISS.INFOADDRESS,ISS.DESCRIPTION,A3.<@CDCONTENT> STATUS,
    (CASE WHEN STATUS IN (''P'') THEN ''Y'' ELSE ''N'' END) APRALLOW
FROM ISSUERS ISS, (SELECT * FROM ALLCODE WHERE CDTYPE = ''SA'' AND CDNAME = ''ECONIMIC'') A0,
(SELECT * FROM ALLCODE WHERE CDTYPE = ''SA'' AND CDNAME = ''BUSINESSTYPE'') A1,ALLCODE A2,ALLCODE A3,
(SELECT BO.TICKERLIST,ISS.ISSUERID
        FROM BONDTYPE BO, ISSUES ISS
        WHERE BO.ISSUESID = ISS.AUTOID AND BO.SECTYPE=''555'') BSE 
WHERE ECONIMIC = A0.CDVAL (+) 
AND  BUSINESSTYPE=A1.CDVAL (+) AND BSE.ISSUERID= ISS.ISSUERID  
AND A2.CDTYPE = ''SA'' AND A2.CDNAME = ''MARKETSIZE'' AND A2.CDVAL=MARKETSIZE 
AND A3.CDTYPE =''SA'' AND A3.CDNAME=''STATUS''  and a3.cdval= STATUS', 'ISSUERS', 'frmISSUERS', '', '', NULL, 5000, 'N', 30, '', 'Y', 'T', '', 'N', '');COMMIT;