SET DEFINE OFF;DELETE FROM SEARCH WHERE 1 = 1 AND NVL(SEARCHCODE,'NULL') = NVL('ISSUESALL','NULL');Insert into SEARCH   (SEARCHCODE, SEARCHTITLE, EN_SEARCHTITLE, SEARCHCMDSQL, OBJNAME, FRMNAME, ORDERBYCMDSQL, TLTXCD, CNTRECORD, ROWPERPAGE, AUTOSEARCH, INTERVAL, AUTHCODE, ROWLIMIT, CMDTYPE, CONDDEFFLD, BANKINQ, BANKACCT) Values   ('ISSUESALL', 'Quản lý đợt phát hành trái phiếu', 'Bond issue management ', 'SELECT ISS.AUTOID, ISS.ISSUERID, ISR.SHORTNAME, ISR.FULLNAME, ISS.ISSUECODE,ISS.ISSUEVAL, ISS.VALUEOFISSUE,
    ISS.ISSUEDATE, A1.<@CDCONTENT> TYPERATE, ISS.LTVRATE, ISS.MAXLTVRATE, ISS.WARNINGLTVRATE, ISS.MATURITYDATE,
    (CASE WHEN ISS.STATUS IN (''P'') THEN ''Y'' ELSE ''N'' END) APRALLOW
FROM ISSUES ISS 
JOIN ISSUERS ISR ON ISS.ISSUERID = ISR.ISSUERID
JOIN ALLCODE A1 ON A1.CDTYPE = ''CB'' AND A1.CDNAME = ''TYPERATE'' AND A1.CDUSER = ''Y'' AND A1.CDVAL = ISS.TYPERATE', 'ISSUES', NULL, 'ISS.AUTOID', NULL, NULL, 5000, 'Y', 1, NULL, 'Y', 'T', NULL, 'N', NULL);COMMIT;