    A0.CDCONTENT DESC_STATUS, RF.CUSTID, RF.AFACCTNO, RF.EFFDATE, RF.EXPDATE, RG.REFRECFLNKID REGRPID
--AnTB them RG.REFRECFLNKID REGRPID nhom hien tai cua moi neu co
FROM RECFLNK RF, CFMAST CF, ALLCODE A0,BRGRP, REGRPLNK RG, tlprofiles tl
WHERE A0.CDTYPE=''RE'' AND A0.CDNAME=''STATUS'' AND A0.CDVAL=RF.STATUS AND RF.CUSTID=CF.CUSTID
