    and RF.CUSTID = RG.CUSTID(+)', 'RECFLNK', 'frmRECFLNK', 'FULLNAME', NULL, 0, 5000, 'N', 1, NULL, 'Y', 'T', NULL, 'N', NULL);COMMIT;