SET DEFINE OFF;DELETE FROM SEARCH WHERE 1 = 1 AND NVL(SEARCHCODE,'NULL') = NVL('TTDIENRHIST','NULL');Insert into SEARCH   (SEARCHCODE, SEARCHTITLE, EN_SEARCHTITLE, SEARCHCMDSQL, OBJNAME, FRMNAME, ORDERBYCMDSQL, TLTXCD, CNTRECORD, ROWPERPAGE, AUTOSEARCH, INTERVAL, AUTHCODE, ROWLIMIT, CMDTYPE, CONDDEFFLD, BANKINQ, BANKACCT) Values   ('TTDIENRHIST', 'Điện nhận từ VSD', 'Receive from VSD  ', 'SELECT c.CDCONTENT CAPTION,log.REFMSGID REFAUTOID,
case when dtl.fldname = ''REFCORPID'' and SUBSTR(DTL.FLDVAL,1,4) = ''PCTI'' then SUBSTR(DTL.FLDVAL,6)
        when dtl.fldname = ''REFCORPID'' and SUBSTR(DTL.FLDVAL,1,4) <> ''PCTI'' then ''''
        when dtl.fldname = ''TRADINGDATE'' and SUBSTR(DTL.FLDVAL,1,4) = ''TRAD'' then SUBSTR(DTL.FLDVAL,6)
        when dtl.fldname = ''TRADINGDATE'' and SUBSTR(DTL.FLDVAL,1,4) <> ''TRAD'' then ''''
        else DTL.FLDVAL end VALUE
FROM VSDTRFLOGDTLHIST DTL, VSDTRFLOGHIST LOG,
    (select fldname, caption CDCONTENT, en_caption EN_CDCONTENT from vsdtrfcaption) c
WHERE LOG.AUTOID=DTL.REFAUTOID
and c.fldname = dtl.fldname
AND LOG.AUTOID=to_char(''<$KEYVAL>'') ORDER BY DTL.AUTOID', 'ST.TTDIEN', 'frmRECFDEF', NULL, NULL, 0, 5000, 'N', 1, NULL, 'Y', 'T', NULL, 'N', NULL);COMMIT;